-- create "contract_proposals" table
CREATE TABLE "contract_proposals" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "create_time" timestamptz NOT NULL, "update_time" timestamptz NOT NULL, "proposal_id" bigint NOT NULL, "title" character varying NOT NULL, "description" character varying NOT NULL, "first_seen_time" timestamptz NOT NULL, "voting_end_time" timestamptz NOT NULL, "status" character varying NOT NULL, "chain_contract_proposals" bigint NULL, PRIMARY KEY ("id"), CONSTRAINT "contract_proposals_chains_contract_proposals" FOREIGN KEY ("chain_contract_proposals") REFERENCES "chains" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
